import React from "react";
import {MainContainer} from "./components/MainContainer";
import Typography from "@material-ui/core/Typography";
import Checkbox from '@material-ui/core/Checkbox';
import {Form} from "./components/Form";
import {useForm} from "react-hook-form";
import {useHistory} from 'react-router-dom';
import {Input} from "./components/Input";
import {PrimaryButton} from "./components/PrimaryButton";
import {yupResolver} from "@hookform/resolvers/yup";
import * as yup from 'yup';
import {FormControlLabel} from "@material-ui/core";

const schema = yup.object().shape({
    email: yup.string().email("Email should have correct format").required('This is required')
})

export const Step2 = () => {
    const history = useHistory()
    const {register, handleSubmit, formState: {errors}, watch} = useForm({
        mode: 'onBlur',
        resolver: yupResolver(schema)
    })

    const hasPhone = watch('hasPhone')
    const testingPhone = watch('testPhone')

    const onSubmit = (data) => {
        console.log(data)
        history.push('/step3')
    }
    return (
        <MainContainer>
            <Typography component={'h2'} variant={'h5'}>
                Step twoüêó
            </Typography>
            <Form onSubmit={handleSubmit(onSubmit)}>
                <Input {...register('email', {required: true})}
                       id={'email'}
                       type={'email'}
                       label={'Email'}
                       name={'email'}
                       error={!!errors.email}
                       helperText={errors?.email?.message}
                       required/>
            </Form>
            <FormControlLabel
                control={
                    <Checkbox name={'hasPhone'} defaultValue={false} defaultChecked={false} {...register('hasItPhone')} color={'primary'}/>
                }
                label={'Do you have a phone?'}
            />
            <input type={'checkbox'} {...register('testPhone')} />
            {
                hasPhone && (
                    <Input name={'phoneNumber'} {...register('phoneNumber')} id={'phoneNumber'} type={'tel'} label={'Phone number'} />
                )
            }
            {testingPhone && <input type={'number'}/>}
            <PrimaryButton>Next</PrimaryButton>
        </MainContainer>
    );
}
