import React, {useState} from "react";
import {MainContainer} from "./components/MainContainer";
import Typography from "@material-ui/core/Typography";
import {useData} from "./DataContext";
import {makeStyles, MuiThemeProvider, TableContainer} from "@material-ui/core";
import {Paper} from "material-ui";
import TableHead from "@material-ui/core/TableHead";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import TableCell from "@material-ui/core/TableCell";
import TableRow from "@material-ui/core/TableRow";
import TableBody from "@material-ui/core/TableBody";
import {Link} from "react-router-dom";
import Table from "@material-ui/core/Table";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import {InsertDriveFile} from "@material-ui/icons";
import ListItemText from "@material-ui/core/ListItemText";
import {PrimaryButton} from "./components/PrimaryButton";
import Swal from 'sweetalert2';
import Confetti from "react-confetti";
// import useWindowSize from 'react-use/lib/useWindowSize'

const useStyles = makeStyles((theme) => ({
    root: {
        marginBottom: "30px"
    },
    table: {
        marginBottom: "30px"
    },
}));
export const Result = () => {
    const [success, setSuccess] = useState(false);
    const {data} = useData()
    const entries = Object.entries(data).filter((entry) => entry[0] !== 'files')
    const {files} = data;
    const myStyle = useStyles()

    const onSubmit = async () => {
        const formData = new FormData();
        // const { width, height } = useWindowSize()
        Swal.fire("Great job!", "You've passed all data!", "success")
        setSuccess(true)
        if (true) {
            console.log('here is confetti')
            return <Confetti width={"500px"}
                             height={"300px"}/>
        }
        // if (data.files) {
        //     data.files.forEach(file => {
        //         formData.append("files", file, file.name)
        //     })
        // }
        //
        // entries.forEach(entry => {
        //     formData.append(entry[0], entry[1])
        // })
        //
        // const res = await fetch("http://localhost:4000/", {
        //     method: "POST",
        //     body: formData
        // })
        //
        // if(res.status==200){
        //     Swal.fire("Great job!","You've passed all data", "success")
        // }
    }

    return (
        <MainContainer>
            <Typography component={'h2'} variant={'h5'}>
                Form data 💖
            </Typography>
            <MuiThemeProvider>
                <TableContainer className={myStyle.root} component={"div"}>
                    <Table className={myStyle.table}>
                        <TableHead>
                            <TableRow>
                                <TableCell>
                                    Field
                                </TableCell>
                                <TableCell align={'right'}>
                                    Value
                                </TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {
                                entries.map(entry => (
                                    <TableRow key={entry[0]}>
                                        <TableCell>{entry[0]}</TableCell>
                                        <TableCell align={'right'}>{entry[1]?.toString()}</TableCell>
                                    </TableRow>
                                ))
                            }
                        </TableBody>
                    </Table>
                </TableContainer>
                {
                    files &&
                    <>
                        <Typography component={'h2'} variant={'h5'}>
                            Files 🐧
                        </Typography>
                        <List>
                            {
                                files.map((f, index) => (
                                    <ListItem key={index}>
                                        <ListItemIcon>
                                            <InsertDriveFile/>
                                        </ListItemIcon>
                                        <ListItemText primary={f.name} secondary={f.size}/>
                                    </ListItem>
                                ))
                            }
                        </List>
                    </>
                }
            </MuiThemeProvider>
            <Confetti width={"500px"}
                             height={"300px"}/>
            <PrimaryButton onClick={onSubmit}>Submit</PrimaryButton>
            <Link to={'/'}>To the start</Link>
        </MainContainer>
    );
}
