import React, {useEffect, useState} from "react";
import {MainContainer} from "./components/MainContainer";
import axios from "axios";
import Typography from "@material-ui/core/Typography";
import {MuiThemeProvider, TableContainer} from "@material-ui/core";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TableBody from "@material-ui/core/TableBody";
import makeStyles from "@material-ui/core/styles/makeStyles";
import {useHistory} from "react-router-dom";
import {Form} from "./components/Form";
import {PrimaryButton} from "./components/PrimaryButton";


const useStyles = makeStyles((theme) => ({
    root: {
        marginBottom: "30px",
        textAlign: "center",
    },
    table: {
        marginBottom: "30px"
    },
}))

export const Main = () => {
    const history = useHistory()
    const [data, setData] = useState([])
    const myStyle = useStyles()

    const onSubmit = () => {
        history.push('/step1')
    }

    const res = useEffect(() => {
        axios({
            method: "GET",
            url: "http://127.0.0.1:8000/api/card"
        }).then(response => {
            setData(response.data ?? [])
        })
    },[])
    return (
        <MainContainer>
            <Typography component={'h2'} variant={'h4'}>
                All user cards ðŸ¥³ðŸŽƒ
            </Typography>
            <ul>
                { data && data.map(p => (
                    <li key={p.id}>{p.firstName}--{p.email}</li>
                ))}
            </ul>
            <MuiThemeProvider>
                <TableContainer className={myStyle.root} component={"div"}>
                    <Table className={myStyle.table}>
                        <TableHead>
                            <TableRow>
                                <TableCell>
                                    Field
                                </TableCell>
                                <TableCell align={'right'}>
                                    Value
                                </TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {data.map(p => (
                                <TableRow key={p.id}>
                                    <TableCell>{p.firstName}</TableCell>
                                    <TableCell>{p.lastName}</TableCell>
                                    <TableCell align={'right'}>{p.phoneNumber?.toString()}</TableCell>
                                </TableRow>
                            ))}
                            {/*{*/}
                            {/*    entries.map(entry => (*/}
                            {/*        <TableRow key={entry[0]}>*/}
                            {/*            <TableCell>{entry[0]}</TableCell>*/}
                            {/*            <TableCell align={'right'}>{entry[1]?.toString()}</TableCell>*/}
                            {/*        </TableRow>*/}
                            {/*    ))*/}
                            {/*}*/}
                        </TableBody>
                    </Table>
                </TableContainer>
            </MuiThemeProvider>
            <Form onSubmit={onSubmit}>
                <PrimaryButton>Add user card</PrimaryButton>
            </Form>
        </MainContainer>
    );
}
