import React from 'react';
import {MainContainer} from "./components/MainContainer";
import {Typography} from "@material-ui/core";
import {Form} from "./components/Form";
import {Input} from './components/Input';
import {useForm} from "react-hook-form";
import {PrimaryButton} from "./components/PrimaryButton";
import * as yup from 'yup';

const schema = yup.object().shape({
    firstName: yup.string().matches(/^([^0-9]*)$/,"First name should not contain numbers").required("First name is required"),
    lastName: yup.string().matches(/^([^0-9]*)$/,"Last name should not contain numbers").required("Last name is required")
})


export const Step1 = () => {
    const {register, handleSubmit, error} = useForm({
        mode: 'onBlur'
    })

    const onSubmit = (data) => {
        console.log(data)
    }
    return <MainContainer>
        <Typography component={'h2'} variant={'h5'}>
            Step oneðŸ”¥
        </Typography>
        <Form onSubmit={handleSubmit(onSubmit)}>
            <Input {...register('firstName', {required: true})} id={'FirstName'} type={"text"} label={"First Name"}/>
            <Input {...register('lastName', {required: true})} id={'LastName'} type={"text"} label={"Last Name"}/>
            <PrimaryButton>Next step</PrimaryButton>
        </Form>
    </MainContainer>
}
